import React, { useState } from 'react';
import auth0 from '../lib/auth0';

const CreateStatus = () => {
  const [dados, setDados] = useState({
    status: 'bem',
    coords: {
      lat: null,
      long: null,
    }
  })
  const getMyLocation = () => {
    //se o navegador tiver a funcionalidade de geolocalização
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(position => {
        //define uma função que pega a versão antiga dos dados e retorna uma versão nova
        setDados(old => {
          return {
            //pega tudo que já tava na variável dados
            ...old,
            coords:{
              lat: position.coords.latitude,
              long: position.coords.longitude,
            }
          }
        })
      });
    }
  };

  return (
    <div>
      <h1>Create Status</h1>

      <label className='block'>
        <input type='radio' name='status' value='bem' />Estou bem e sem sintomas.
      </label>

      <label className='block'>
        <input type='radio' name='status' value='gripe' />Estou com sintomas de gripe / resfriado.
      </label>

      <label className='block'>
        <input type='radio' name='status' value='covid' />Estou com sintomas de COVID.
      </label>

      Sua posição atual: {JSON.stringify(dados)}
      <button onClick={getMyLocation}>Get my location</button>
    </div>
  );
};
export default CreateStatus;

//verificar se o usuario ta logado
export async function getServerSideProps({ req, res }) {
  const session = await auth0.getSession(req);
  if (session) {
    return {
      props: {
        isAuth: true,
        user: session.user,
      }
    }
  }
  return {
    props: {
      isAuth: false,
      user: {}
    }
  }
}
